import{_ as t,c as d,o as a,a4 as e}from"./chunks/framework.Br2U662V.js";const b=JSON.parse('{"title":"方案评估","description":"","frontmatter":{},"headers":[],"relativePath":"Design/Evaluation.md","filePath":"Design/Evaluation.md"}'),r={name:"Design/Evaluation.md"},i=e('<h1 id="方案评估" tabindex="-1">方案评估 <a class="header-anchor" href="#方案评估" aria-label="Permalink to &quot;方案评估&quot;">​</a></h1><h3 id="_1-游戏类目" tabindex="-1">1. 游戏类目 <a class="header-anchor" href="#_1-游戏类目" aria-label="Permalink to &quot;1. 游戏类目&quot;">​</a></h3><p>考虑到游戏体积与逻辑复杂度，目前建议中轻度2D/3D游戏进行转换，游戏类目包括：</p><ul><li>休闲：消除，答题，模拟经营，塔防，益智等</li><li>动作：跑酷，飞行设计，轻度IO</li><li>棋牌：棋类，牌类</li><li>角色：卡牌，回合，策略</li></ul><p>对重度游戏如MMO/FPS等游戏需根据实际情况评估</p><h3 id="_2-引擎版本" tabindex="-1">2. 引擎版本 <a class="header-anchor" href="#_2-引擎版本" aria-label="Permalink to &quot;2. 引擎版本&quot;">​</a></h3><p>版本建议：</p><p>Unity版本支持从2018到最新的2022版本</p><p>建议使用LTS版本，尽量使用2022(更快编译和更小体积、压缩纹理、压缩音频等支持)</p><h3 id="_3-能力支持" tabindex="-1">3. 能力支持 <a class="header-anchor" href="#_3-能力支持" aria-label="Permalink to &quot;3. 能力支持&quot;">​</a></h3><table><thead><tr><th>能力</th><th>是否支持</th><th>解决方案</th></tr></thead><tbody><tr><td>Unity基础模块</td><td>支持</td><td>支持动画、物理、AI、UI等基础模块</td></tr><tr><td>渲染管线</td><td>支持</td><td>支持标准渲染管线、URP</td></tr><tr><td>资源加载</td><td>支持</td><td>Addressable、AssetBundle网络异步加载</td></tr><tr><td>Lua脚本</td><td>支持</td><td>支持标准Lua与常见binding(如xlua, tolua等)， 不支持Luajit。需根据实际游戏在真机验证性能</td></tr><tr><td>PureTS</td><td>支持</td><td>iOS系统需14.5以上, 支持JIT</td></tr><tr><td>Unity音频</td><td>支持</td><td>UnityAudio基本能力支持，支持fmod插件(长音频请使用UnityAudio), 但不支持wwise。</td></tr><tr><td>第三方插件</td><td>部分支持</td><td>支持大部分插件，C#插件与非平台相关的C原生插件</td></tr><tr><td>网络系统</td><td>需调整</td><td>HTTP使用UnityWebRequest，TCP使用WX SDK适配或WebSocket通信替代(如开源的UnityWebSocket插件)，UDP使用WX SDK适配</td></tr><tr><td>渲染接口</td><td>支持</td><td>仅支持WebGL1.0(相当于OpenGLES2.0)，2.0支持处于Beta测试阶段</td></tr><tr><td>多线程</td><td>不支持</td><td>删除多线程用法，使用异步等其他替代方式</td></tr><tr><td>文件API</td><td>需调整</td><td>不支持System.File接口，但可使用微信小游戏WX SDK实现文件存储，<a href="./FileCache.html">大小限制为200MB(最高可开通1GB)</a>。玩家存档请使用服务器或云开发；AssetsBundle缓存适配插件已自动支持</td></tr></tbody></table>',11),o=[i];function l(n,s,h,_,c,u){return a(),d("div",null,o)}const m=t(r,[["render",l]]);export{b as __pageData,m as default};
